import { useEffect, useState } from "react";

const MealsListCocktails = () => {
    const [meals, setMeals] = useState([]);
    const [detailMeal, setDetailMeal] = useState(null);

    const fetchMeals = async () => {
        const responseApi = await fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s= ");
        const responseJson = await responseApi.json();

        setMeals(responseJson.meals);
    };

    const handleDetailsClick = async (idMeal) => {
        const responseApi = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idMeal}`);
        const responseJson = await responseApi.json();

        setDetailMeal(responseJson.meals[0]);
    };

    useEffect(() => {
        fetchMeals();
    }, []);

    return (
        <div>
            <h1>Meals List</h1>

        </div>
    );
};

export default MealsListCocktails;
